INSERT INTO `wp_posts` VALUES (66,1,'2025-02-13 11:16:40','2025-02-13 11:16:40','<!-- wp:paragraph -->\n<p><strong>New Year 2024 Celebration</strong> is a festive event to welcome the upcoming year with joy and excitement. Held on <strong>December 31, 2023</strong>, from <strong>8:00 PM to 12:30 AM</strong> at the <strong>City Center Plaza</strong>, the evening features live music, dancing, delicious food, and a countdown to midnight. Enjoy dazzling fireworks, fun activities, and a vibrant atmosphere as we bid farewell to 2023 and embrace 2024 with friends, family, and the community. Don’t miss this unforgettable night of celebration and new beginnings</p>\n<!-- /wp:paragraph -->','New Year 2024','','publish','closed','closed','','new-year-2024','','','2025-02-13 11:16:40','2025-02-13 11:16:40','',0,'http://fictional-university.local/?post_type=event&#038;p=66',0,'event','',0);
INSERT INTO `wp_posts` VALUES (67,1,'2025-02-13 12:22:13','2025-02-13 12:22:13','<!-- wp:paragraph -->\n<p><strong>Math Program</strong> is an engaging and interactive initiative designed to enhance students\' mathematical skills and problem-solving abilities. Held on <strong>November 20, 2023</strong>, from <strong>3:00 PM to 5:00 PM</strong> in the <strong>School Auditorium</strong>, the program includes workshops, hands-on activities, and expert-led sessions to make math fun and accessible. Whether you\'re a beginner or advanced learner, this event offers something for everyone, fostering a deeper understanding of mathematical concepts. Join us to explore the wonders of math and boost your confidence in this essential subject!</p>\n<!-- /wp:paragraph -->','Math','','publish','closed','closed','','math','','','2025-02-26 04:53:26','2025-02-26 04:53:26','',0,'http://fictional-university.local/?post_type=program&#038;p=67',0,'program','',0);
